import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { useState } from 'react';
import { StyleSheet, Text, View, Button, TextInput, Component } from 'react-native';

import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

export default function EnterName({ navigation }) {

    const [name, setName] = useState('');

    return (
    <View style={styles.container}>
        <Text style={styles.heading}>What can we call you?</Text>
        <TextInput style={styles.input}
        placeholder='e.g. Elias' 
        onChangeText={(text) => {
            setName(text)
            console.log(name)}}
            />
        <StatusBar style="auto" />

        <Button
        onPress={() => navigation.navigate('CreationScreen')}
        title="Next"
        color="#ab3535"
        accessibilityLabel="Learn more about this purple button"
        />
    </View>

    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#33b5e5',
        alignItems: 'center',
        justifyContent: 'center',
    },
    heading: {
        fontSize: 25,
        textAlign: 'center',
        color: '#000000',
    },
    input: {
        borderWidth: 1,
        borderColor: '#b5e5f7',
        borderRadius: 12,
        padding: 7,
        paddingLeft: 20,
        margin: 15,
        width: "60%",
        fontSize: 20,
        marginBottom:'50%',
        backgroundColor: '#b5e5f7',
    },
});
