import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { useState } from 'react';
import {StyleSheet, View, Button } from 'react-native';
import {Text, TextInput} from 'react-native-paper';

export default class EnterName extends Component {

    state = {
        text: ''
    }
    render() {
        return (
            <View style={{flex: 1, backgroundColor: "#499ab8"}}>
                <View style={{flex:0.3}}>
                </View>
                <View style={{ flex: 0.5, padding: "15%"}}>
                <Text style={{fontSize: 25}}> What can we call you? {"\n"}</Text>
                <TextInput style={{backgroundColor: "#9bc3d1"}} mode="flat" label="Name" value={this.state.text}
                onChangeText={text => this.setState({text})} />
                <View style={{flex: 0.1}}></View>
                <View style={{width: "20%", marginLeft: "80%"}}>
                <Button mode="contained" onPress={() => this.props.navigation.navigate('CreationScreen')} title="Next" style={{backgroundColor: "#a3a3a3"}}>
                </Button></View>
                </View>
            </View>
        );
    }
}
