import { StatusBar } from 'expo-status-bar';
// import React, {Component} from 'react';
// import {useState, Component, React } from 'react';
import * as React from 'react';
// import { StyleSheet, Text, View, Button, Alert } from 'react-native';
import {StyleSheet, SafeAreaView, View } from 'react-native';
import {Provider as PaperProvider , Appbar, Text, BottomNavigation, TextInput} from 'react-native-paper';



export default function CreationScreen() {

    const [text, setText] = React.useState('');



    const MusicRoute = () => <Text>Music</Text>;

    const AlbumsRoute = () => <Text>Albums</Text>;

    const RecentsRoute = () => <Text>Recents</Text>;

    const [index, setIndex] = React.useState(0);
    const [routes] = React.useState([
      { key: 'music', title: 'Music', icon: 'queue-music' },
      { key: 'albums', title: 'Albums', icon: 'album' },
      { key: 'recents', title: 'Recents', icon: 'history' },
    ]);

    const renderScene = BottomNavigation.SceneMap({
        music: MusicRoute,
        albums: AlbumsRoute,
        recents: RecentsRoute,
    });




    return (
        // <View style={styles.container}>
        //   <Text style={styles.heading}> text</Text>
        //   <Text>find your productivity</Text>
        //   <View style={[{margin:10, width:"40%"}]}>
        //   <Button
        //     onPress={() => Alert.alert("hello")}
        //     title="Get Started"
        //     color="#ab3535"
        //     accessibilityLabel="Learn more about this purple button"
        //   />
        //   <StatusBar style="auto" />
        //   </View>
        // </View>
        <PaperProvider>

            <SafeAreaView>
                <Appbar style={styles.bottom}>
                <Appbar.Action
                    icon="archive"
                    onPress={() => console.log('Pressed archive')}
                    />
                    <Appbar.Action icon="mail" onPress={() => console.log('Pressed mail')} />
                    <Appbar.Action icon="label" onPress={() => console.log('Pressed label')} />
                    <Appbar.Action
                    icon="delete"
                    onPress={() => console.log('Pressed delete')}
                    />
                </Appbar>
            </SafeAreaView>
            
            <View style={{padding:20}}>
                <Text style={styles.heading}>Create a Study Session</Text>
            </View>
            <View style={{paddingLeft:20}}>
                <Text style={{fontSize:30}}>Subject:</Text>
            </View>


            <View style={{padding:20, paddingTop:5}}>
                <TextInput
                    label="What will you be doing?"
                    value={text}
                    onChangeText={text => setText(text)}
                    mode = "outlined"
                />
            </View>


            <BottomNavigation
                navigationState={{ index, routes }}
                onIndexChange={setIndex}
                renderScene={renderScene}
            />






            
        </PaperProvider>

        

        );
    }

    
    const styles = StyleSheet.create({
        container: {
            flex: 1,
            backgroundColor: '#33b5e5',
            alignItems: 'center',
            justifyContent: 'center',
        },
        heading: {
            padding: 20,
            fontSize: 60,
            textAlign: 'center',
        },
        input: {
            padding: 20,
            fontSize: 60,
            textAlign: 'center',
        },
      });
