import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, Button, Alert, SafeAreaView, ScrollView, FlatList, TouchableHighlight, TouchableOpacity} from 'react-native';

export default function HomePage() {

    //temp place holder for data
    //startTime should an int, so its easier to use with an clock/time api idk
    //I just left it as a string so we can output am/pm 
    let [sess, setSess] = useState([
      {name: "math", starTime: "3pm", time: 2, sessName: "Pomodorox2", key: 1},
      {name: "bio", starTime: "3pm", time: 2, sessName: "Pomodorox2", key: 2},
      {name: "paper", starTime: "4pm", time: 2, sessName: "Day-ly grind", key: 3},  
      {name: "exam", starTime: "5pm", time: 2, sessName: "52-17", key: 4},
    ])

    const handleEdit = () => {
      Alert.alert("Fuk u")
    }

    return (
    <View style={styles.container}>
      <Text style={styles.heading}> 
          Welcome, Theodore
      </Text>
      <Text style ={styles.subHeading}> Here are your day.ly tasks :)</Text>

      {/* List of Sessions*/}
      <FlatList
      keyExtractor={item => item.key.toString()}
        data={sess}
        renderItem={({ item }) => (
          <TouchableOpacity onLongPress={() => handleEdit()}>
            <Text style={styles.item}>{item.name + " - " + item.starTime}</Text>
          </TouchableOpacity>
        )}
      />
      
    </View>
    );
}
    
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f1e9db',
    paddingHorizontal: 20,
    paddingTop: 50,
  },
  heading: {
    fontSize: 40,
    textAlign: 'center',

  },
  subHeading: {
    fontSize: 25,
    textAlign: 'center',
    paddingBottom: 40,
    
  },
  item: {
    marginTop: 25,
    padding: 30,
    backgroundColor: '#a39b8b',
    fontSize: 24,
    color: "black",
  },
  scrollView:{
    marginHorizontal: 20,
    marginVertical: 50,
  },
});
